create table t {
    schema {
        cstring a[1000]
        int b
    }
    keys {
        datacopy(a) "b" = b
    }
};$$

select * from comdb2_keys where tablename='t';
select * from comdb2_partial_datacopies where tablename='t';

insert into t values ('', 3);

select a from t where b < 40;
select length(a) from t where b < 40;

explain query plan select a from t where b < 40;

update t set a = '' where b = 3;

select a from t where b < 40;
select length(a) from t where b < 40;

explain query plan select a from t where b < 40;

drop table t;
